pragma solidity ^0.4.24;

import './SafeMath.sol'; 
import './Trust.sol'; 

// @notice This contract allows someone to leave ETH for a beneficiary
// @notice assumes each beneficiary receives equal amount 
// TODO: test if this saves any gas
contract Beneficiaries { 
  using SafeMath for uint; 

  Trust public trust; 

  address public operator; 

  uint public numBenefactors; 
  bytes32 public benefactorHash = keccak256("helloworld");

  mapping (address => bool) public checkPoints;  

  uint public distributionAmount; 
  mapping (address => uint) public amountRedeemed; 




  // @param (address) _beneficiary = The ETH address of who is to receive the distributionAmount
  // @notice can only hold up to 50 beneficiaries
  // TODO: move this out of constructor
  constructor(address[] _beneficiaries, address _trust)
  public 
  payable { 
    require(_beneficiaries.length < 50); 
    operator = msg.sender; 
    trust = Trust(_trust); 
    checkPoints[_beneficiaries[0]] = true;  
    checkPoints[_beneficiaries[_beneficiaries.length]] = true; 
    benefactorHash = keccak256(abi.encodePacked(_beneficiaries[0], benefactorHash)); 
    benefactorHash = keccak256(abi.encodePacked(_beneficiaries[_beneficiaries.length], benefactorHash)); 
    for (uint i =1; i < _beneficiaries.length - 1; i++){ 
      benefactorHash = keccak256(abi.encodePacked(_beneficiaries[i], benefactorHash)); 
      LogBeneficiary(_beneficiaries[i]);
    }
    distributionAmount = msg.value; 
  }


  function makeClaim()
  external 
  returns (bool) { 
    uint claimAmount = trust.distributionAmount(); 
    require(trust.redeem()); 
    distributionAmount = distributionAmount.add(claimAmount); 
    return true;
  }


  function withdraw(address[] _beneficiaries)
  external 
  returns (bool) { 
    require(isABeneficiary(msg.sender, _beneficiaries)); 
    uint amount = distributionAmount.div(_beneficiaries.length).sub(amountRedeemed[msg.sender]); 
    amountRedeemed[msg.sender] = amountRedeemed[msg.sender].add(amount); 
    msg.sender.transfer(amount); 
  }


  function isABeneficiary(address _user, address[] _beneficiaries)
  public 
  returns (bool isBeneficiary) { 
    bytes32 verificationHash = keccak256("helloworld"); 
    require(checkPoints[_beneficiaries[0]] && checkPoints[_beneficiaries[_beneficiaries.length]]); 
    for (uint i = 0; i < _beneficiaries.length; i++) { 
      verificationHash = keccak256(abi.encodePacked(verificationHash, _beneficiaries[i]));
      if (_beneficiaries[i] == _user) { isBeneficiary = true; }
    }
    return isBeneficiary; 
  }

  event LogBeneficiary(address _beneficiary); 

  }
